import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../App.css";
import axios from "axios";
import { useNavigate } from 'react-router-dom';

const AddEmployee = (props) => {
  // Define the state with useState hook
  const navigate = useNavigate();
  const [employee, setEmployee] = useState({
    full_name: '',
    gender: '',
    email: '',
    phone: '',
    organization: '',
    designation: '',
    salary: '',
    address: '',
  });
  const [errors, setErrors] = useState({
    full_name: '',
    gender: '',
    email: '',
    phone: '',
    organization: '',
    designation: '',
    salary: '',
    address: '',
  });
  const onChange = (e) => {
    const { name, value } = e.target;

  // Regular expressions for validation
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const phoneRegex = /^[6-9]\d{9}$/;
  const panRegex = /^[a-zA-Z]{3}[P|p][a-zA-Z]{1}[0-9]{4}[a-zA-Z]{1}$/;
  const full_nameRegex =  /^[a-zA-Z ]+$/;
  const addressRegex = /[a-zA-Z0-9\s,./]+$/ ;
  const pincodeRegex = /^[1-9]{1}[0-9]{5}$/;

   // Validate inputs and set error messages
   switch (name) {
    case 'full_name':
      setErrors({ ...errors, full_name: value ? '' : 'Full Name is required' });
      break;
    case 'gender':
      setErrors({ ...errors, gender: value ? '' : 'Gender is required' });
      break;
    case 'email':
      setErrors({
        ...errors,
        email: emailRegex.test(value) ? '' : 'Invalid email address',
      });
      break;
    case 'phone':
      setErrors({
        ...errors,
        phone: phoneRegex.test(value) ? '' : 'Invalid phone number',
      });
      break;
    default:
      break;
  }

  // Update employee state
  setEmployee({ ...employee, [name]: value });
  };

  const onSubmit = (e) => {
    e.preventDefault();

    axios
      .post('http://localhost:4000/api/employees', employee)
      .then((res) => {
        setEmployee({
          full_name: '',
          gender: '',
          email: '',
          phone: '',
          organization: '',
          designation: '',
          salary: '',
          address: '',
        });

        // Push to /
        navigate('/');
      })
      .catch((err) => {
        console.log('Error in Adding Employee!');
      });
  };

  return (
    <div className='AddEmployee'>
      <div className='container'>
        <div className='row'>
          <div className='col-md-8 m-auto'>
            <br />
            <Link to='/' className='btn btn-outline-warning float-left'>
              Show Employee List
            </Link>
          </div>
          <div className='col-md-8 m-auto'>
            <h1 className='display-4 text-center'>Add Employee</h1>
            <p className='lead text-center'>Create new Employee Profile</p>

            <form noValidate onSubmit={onSubmit}>
              <div className='form-group'>
                <input
                  type='text'
                  placeholder='Name'
                  name='full_name'
                  className='form-control'
                  value={employee.full_name}
                  onChange={onChange}
                />
              </div>
              <div className='form-group'>
                <input
                  type='text'
                  placeholder='Gender'
                  name='gender'
                  className='form-control'
                  value={employee.gender}
                  onChange={onChange}
                />
                <span className='error-message'>{errors.gender}</span>
              </div>
              <div className='form-group'>
                <input
                  type='text'
                  placeholder='Email'
                  name='email'
                  className='form-control'
                  value={employee.email}
                  onChange={onChange}
                />
                <span className='error-message'>{errors.email}</span>
              </div>
              <div className='form-group'>
                <input
                  type='number'
                  placeholder='Phone'
                  name='phone'
                  className='form-control'
                  value={employee.phone}
                  onChange={onChange}
                />
                <span className='error-message'>{errors.phone}</span>
              </div>
              <div className='form-group'>
                <input
                  type='text'
                  placeholder='Organization Name'
                  name='organization'
                  className='form-control'
                  value={employee.organization}
                  onChange={onChange}
                />
              </div>
              <div className='form-group'>
                <input
                  type='text'
                  placeholder='Designation'
                  name='designation'
                  className='form-control'
                  value={employee.designation}
                  onChange={onChange}
                />
              </div>
              <div className='form-group'>
                <input
                  type='number'
                  placeholder='Salary'
                  name='salary'
                  className='form-control'
                  value={employee.salary}
                  onChange={onChange}
                />
              </div>
              <div className='form-group'>
                <input
                  type='text'
                  placeholder='Communication Address'
                  name='address'
                  className='form-control'
                  value={employee.address}
                  onChange={onChange}
                />
              </div>
              <input
                type='submit'
                className='btn btn-outline-warning btn-block mt-4'
              />
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddEmployee;
